import React, { useEffect, useState } from "react";
import { fetchResumes, fetchResumeDetail } from "../api";
import ResumeDetails from "./ResumeDetailModal";

export default function PastResumesTable() {
  const [rows, setRows] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [detail, setDetail] = useState(null);

  useEffect(() => {
    load();
  }, []);

  async function load() {
    try {
      const resp = await fetchResumes();
      setRows(resp.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function openDetail(id) {
    setSelectedId(id);
    const resp = await fetchResumeDetail(id);
    setDetail(resp.data);
  }

  function close() {
    setSelectedId(null);
    setDetail(null);
  }

  return (
    <div>
      <h2>History</h2>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr style={{ textAlign: "left" }}>
            <th>Filename</th><th>Name</th><th>Email</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rows.map(r => (
            <tr key={r.id}>
              <td>{r.filename}</td>
              <td>{r.name}</td>
              <td>{r.email}</td>
              <td><button onClick={() => openDetail(r.id)}>Details</button></td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedId && detail && (
        <ResumeDetailModal onClose={close} data={detail} />
      )}
    </div>
  );
}
