import React, { useState } from "react";
import UploadTab from "./components/UploadTab";
import HistoryTab from "./components/HistoryTab";

function App() {
  const [active, setActive] = useState("upload");
  return (
    <div style={{ maxWidth: 1000, margin: "24px auto", fontFamily: "Arial, sans-serif" }}>
      <h1>Resume Analyzer</h1>
      <div style={{ marginBottom: 12 }}>
        <button onClick={() => setActive("upload")} style={{ marginRight: 8 }}>
          Upload
        </button>
        <button onClick={() => setActive("history")}>History</button>
      </div>
      <div>
        {active === "upload" ? <UploadTab /> : <HistoryTab />}
      </div>
    </div>
  );
}

export default App;
